import axios from 'axios'

const api = store => next => action => {
    if (action.type !== 'apiCallBegan') return next(action)

    const { url, method, data, onSuccess, onError } = action.payload

    axios.request({
        baseURL: `http://localhost:9000`,
        url,
        method,
        data
    })
}

export default api;
