import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import Divider from "@material-ui/core/Divider";
import ListItemText from "@material-ui/core/ListItemText";
import Backdrop from "@material-ui/core/Backdrop";
import Typography from "@material-ui/core/Typography";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import { Modal } from "@material-ui/core";
import { Fade } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { wordAdded } from "../store/dictionarySlice";
import { TextField } from "@material-ui/core";
import { Button } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    maxWidth: "100%",
    maxHeight: "100%",
    marginTop: "50px",
    backgroundColor: theme.palette.background.paper,
    overflow: "auto",
  },
  inline: {
    display: "inline",
  },
  modal: {
    width: "100%",
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    height: "90vh",
    marginTop: "10vh",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  fab: {
    position: "fixed",
    bottom: "20px",
    right: 10,
  },
  addWordForm: {
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    padding: theme.spacing(2, 4, 3),
  },
}));

const WordsList = ({ words }) => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchText, setSearchText] = useState("");
  const dictionary = useSelector((state) => state.dictionary);
  console.log(dictionary);
  const handleClose = () => {
    setOpen(false);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(
      wordAdded({
        word: searchText,
        definition: "definition" + searchText,
      })
    );
    setSearchText("");
    setShowAddForm(false);
  };

  return (
    <>
      <List className={classes.root}>
        {dictionary &&
          dictionary.map((word) => {
            return (
              <React.Fragment key={word.id}>
                <ListItem alignItems="flex-start" onClick={() => setOpen(true)}>
                  <ListItemText
                    primary={word.word}
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          className={classes.inline}
                          color="textPrimary">
                          {word.definition}
                        </Typography>
                      </React.Fragment>
                    }
                  />
                </ListItem>
                <Divider variant="fullWidth" component="li" />
              </React.Fragment>
            );
          })}
      </List>
      <Fab className={classes.fab} color="primary">
        <AddIcon
          onClick={() =>
            // dispatch(wordAdded({ word: "test", definition: "testing" }))
            setShowAddForm(true)
          }>
          Add word
        </AddIcon>
      </Fab>
      {/* Full Screen Modal */}
      <Modal
        className={classes.modal}
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}>
        <Fade in={open}>
          <div className={classes.paper}>
            <h2 id="transition-modal-title">Transition modal</h2>
            <p id="transition-modal-description">
              react-transition-group animates me.
            </p>
          </div>
        </Fade>
      </Modal>

      {/* Add Word Modal */}
      <div className={classes.formContainer}>
        <Modal
          aria-labelledby="transition-modal-title"
          aria-describedby="transition-modal-description"
          className={classes.modal}
          open={showAddForm}
          onClose={() => setShowAddForm(false)}
          closeAfterTransition
          BackdropComponent={Backdrop}
          BackdropProps={{
            timeout: 500,
          }}>
          <Fade in={showAddForm}>
            <div className={classes.addWordForm}>
              <Typography variant="h6">Add To Dictionary</Typography>
              <form onSubmit={handleSubmit}>
                <TextField
                  id="standard-basic"
                  label="New Word"
                  value={searchText}
                  onChange={(e) => setSearchText(e.target.value)}
                />
                <Button onClick={() => setShowAddForm(false)}>Cancel</Button>
                <Button onClick={handleSubmit}>Add</Button>
              </form>
            </div>
          </Fade>
        </Modal>
      </div>
    </>
  );
};

export default WordsList;
